mapper.select:
SELECT  * FROM bj_client

    case_id = #{caseId}
    AND delete_flag = 0

SELECT  DISTINCT case_id AS id FROM bj_client

    client_name = #{clientName}
    AND identity = 0

SELECT  DISTINCT case_id AS id FROM bj_client

    client_name = #{clientName}
    AND identity = 1

mapper.update:
UPDATE bj_client
SET status = 1
WHERE case_id = #{caseId}
